package basicsofjava;

public class If_Else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=200;
		if(a<b)
		{
			System.out.println("true");
		}
		else
		{
			System.out.println("False");
	
		}
		if(a>b) {
			System.out.println("1");

		}
		else
		{
			System.out.println("2");

		}
	}

}
